package week2.day2.homework;

public class StarPattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i = 1; i<6; i++){
           
            for(int j = 1; j<(2*i); j++){
                System.out.print("*");
            }
            System.out.println();
	}

}
}
